import Navigation from '../navigation/Navigation';
import './about_me.css';

const AboutMe = () => {
    const [apropos, setApropos] = useState([]); 

        useEffect(() => {
             const fetchApropos = () => {
                axios
                .get(`${BACK_URL}/a-propos`)
                .then(res => setApropos(res.data[0]))
             }
             fetchApropos()   
         }, []);

    return (
        <div>
            <Navigation/>
            <div  className="section-apropos">
                <div >
