import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';
import {BACK_URL} from '../../http';
import './navigation.css';

const Navigation = () => {
        const [apropos, setApropos] = useState([]); 

        useEffect(() => {
             const fetchApropos = () => {
                axios
                .get(`${BACK_URL}/a-propos`)
                .then(res => setApropos(res.data[0]))
             }
             fetchApropos()   
         }, []);

    return (
        <div className="sidebar">

            <div className="idcContent">
                <h3>{apropos.firstname} {apropos.lastname}</h3>
                <h5> {apropos.title}</h5>
            </div>  

            <ul className="item-navigation">
                <li><Link exact to="/" ClassName="navitems"> <span>Acceuil</span></Link></li>
                <li><Link exact to="/competences" ClassName="navitems"><span>Compétences</span></Link></li>
                <li><Link exact to="/realisations" ClassName="navitems"><span>Réalisations</span></Link></li>
                <li><Link exact to="/experience" ClassName="navitems"><span>Expérience</span></Link>
                </li>
                <li>
                    <Link exact to="/diplomes" ClassName="navitems"><span>Diplômes</span></Link>
                </li>
                <li>
                    <Link exact to="/interets" ClassName="navitems"><span>Intérêts</span></Link>
                </li>
                <li>
                    <Link exact to="/contact" ClassName="navitems"><span>Contact</span></Link>
                </li>
            </ul>

        </div>
    );
};

export default Navigation;
