import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';
import {BACK_URL} from '../../http';
import './navigation.css';

const Navigation = () => {
        const [apropos, setApropos] = useState([]); 

        useEffect(() => {
             const fetchApropos = () => {
                axios
                .get(`${BACK_URL}/a-propos`)
                .then(res => setApropos(res.data[0]))
             }
             fetchApropos()   
         }, []);

    return (
        <div className="sidebar">

            <div className="idcContent">
                <h3>{apropos.firstname} {apropos.lastname}</h3>
                <h5> {apropos.title}</h5>
            </div>  
            <nav>
                <ul className="item-navigation">
                    <li><Link exact to="/"> <span>Acceuil</span></Link></li>
                    <li><Link exact to="/competences" >Compétences</Link></li>
                    <li><Link exact to="/realisations" ClassName="navitems">Réalisations</Link></li>
                    <li><Link exact to="/experience" ClassName="navitems">Expérience</Link></li>
                    <li><Link exact to="/diplomes" ClassName="navitems">Diplômes</Link></li>
                    <li><Link exact to="/interets" ClassName="navitems">Intérêts</Link></li>
                    <li><Link exact to="/contact" ClassName="navitems">Contact</Link></li>
                </ul>
            </nav>

        </div>
    );
};

export default Navigation;
